mapper.select:
select CASE WHEN complaint_type = 0 THEN '其他' WHEN complaint_type = 1 THEN '景区' WHEN complaint_type = 2 THEN '酒店' ELSE '-' END as name, COUNT(id) as value, COALESCE (ROUND(COUNT(id)/total.totalNum, 3)*100,0) as rate from tb_travel_complaint INNER JOIN (select COUNT(id) as totalNum from tb_travel_complaint
) as total ON 1 = 1
GROUP BY complaint_type

select CASE WHEN status = 0 THEN '待指派' WHEN status = 1 THEN '待处理' WHEN status = 2 THEN '已处理' ELSE '-' END as name, COUNT(id) as value, COALESCE (ROUND(COUNT(id)/total.totalNum, 3)*100,0) as rate from tb_travel_complaint INNER JOIN (select COUNT(id) as totalNum from tb_travel_complaint
) as total ON 1 = 1
GROUP BY status

SELECT DATE_FORMAT(a.create_time,#{params.sqlDateFormat}) as time, COUNT(a.id) as count, ROUND(((COUNT(a.id) - (select COUNT(id) from tb_travel_complaint
create_time like concat(left((SELECT DATE_SUB(a.create_time,INTERVAL 1 YEAR)),7),'%') and equip_status = 0 and deleted = 0
))/(select COUNT(id) from tb_travel_complaint
create_time like concat(left((SELECT DATE_SUB(a.create_time,INTERVAL 1 YEAR)),7),'%') and equip_status = 0 and deleted = 0
)),3)*100 as same, ROUND(((COUNT(a.id) - (select COUNT(id) from tb_travel_complaint
create_time like concat(left((SELECT DATE_SUB(a.create_time,INTERVAL 1 MONTH)),7),'%') and equip_status = 0 and deleted = 0
))/(select COUNT(id) from tb_travel_complaint
create_time like concat(left((SELECT DATE_SUB(a.create_time,INTERVAL 1 MONTH)),7),'%') and equip_status = 0 and deleted = 0
)),3)*100 as sequential from tb_travel_complaint a
a.create_time >= CONCAT(YEAR(now()),'-01-01 00:00:00') and a.create_time <= CONCAT(YEAR(now()),'-12-31 23:59:59') and equip_status = 0 and deleted = 0
GROUP BY DATE_FORMAT(a.create_time,#{params.sqlDateFormat})
