mapper.select:
SELECT
COALESCE( `business_type_id`, - 1 ) type,
sum( current_price )/100 current_price
FROM
( SELECT belong_sub_merchant_id, business_type_id, current_price FROM tb_order

    deleted = 0


            and  create_time >= #{params.beginTime} 


            and  create_time <= #{params.endTime} 


            and belong_sub_merchant_id = #{params.subMchId}



) a
INNER JOIN ( SELECT sub_mch_id FROM tb_sub_mch

    deleted = 0


            and place_code LIKE CONCAT(#{params.areaCode},'%')



) b ON a.belong_sub_merchant_id = b.sub_mch_id
GROUP BY
a.business_type_id WITH ROLLUP

SELECT DATE_FORMAT( pay_time,#{params.sqlDateFormat}) AS time, COALESCE ( ROUND( SUM( current_price ) / 100, 2 ), 0 ) AS currentInfo FROM ( SELECT belong_sub_merchant_id, current_price, pay_time FROM tb_order
and pay_time >= #{params.beginTime}
and pay_time <= #{params.endTime}
and deleted = 0
) a INNER JOIN ( SELECT sub_mch_id FROM tb_sub_mch
AND county_code like CONCAT(#{params.areaCode},'%')
and deleted = 0
) b ON a.belong_sub_merchant_id = b.sub_mch_id GROUP BY DATE_FORMAT( pay_time,#{params.sqlDateFormat} )

select CASE WHEN business_type_id = 1 THEN '门票' WHEN business_type_id = 3 THEN '券' WHEN business_type_id = 4 THEN '酒店' WHEN business_type_id = 5 THEN '特产' ELSE '其他' END as name, COUNT(order_id) as value, COALESCE (ROUND(COUNT(order_id)/total.totalNum, 3)*100,0) as rate from tb_order INNER JOIN (select COUNT(order_id) as totalNum from tb_order
) as total ON 1 = 1
GROUP BY business_type_id

select CASE WHEN business_type_id = 1 THEN '门票' WHEN business_type_id = 3 THEN '券' WHEN business_type_id = 4 THEN '酒店' WHEN business_type_id = 5 THEN '特产' ELSE '其他' END as name, ROUND(SUM(current_price)/100,2) as value, COALESCE (ROUND(ROUND(SUM(current_price)/100,2)/total.totalNum, 3)*100,0) as rate from tb_order INNER JOIN (select ROUND(SUM(current_price)/100,2) as totalNum from tb_order
) as total ON 1 = 1
GROUP BY business_type_id
