mapper.select:
SELECT DATE_FORMAT(evaluate.evaluate_time,#{params.sqlDateFormat}) as time, COUNT(evaluate.id) as count, ROUND(((COUNT(evaluate.id) - (select COUNT(evaluateOne.id) from tb_marketing_evaluate evaluateOne
INNER JOIN tb_hotel_info hotel ON hotel.id = evaluateOne.place_id and hotel.status = #{params.status}
evaluateOne.evaluate_time like concat(left((SELECT DATE_SUB(evaluate.evaluate_time,INTERVAL 1 YEAR)),7),'%') and evaluateOne.equip_status = 1 and evaluateOne.deleted = 0
))/(select COUNT(evaluateTwo.id) from tb_marketing_evaluate evaluateTwo
INNER JOIN tb_hotel_info hotel ON hotel.id = evaluateTwo.place_id and hotel.status = #{params.status}
evaluateTwo.evaluate_time like concat(left((SELECT DATE_SUB(evaluate.evaluate_time,INTERVAL 1 YEAR)),7),'%') and evaluateTwo.equip_status = 1 and evaluateTwo.deleted = 0
)),3)*100 as same, ROUND(((COUNT(evaluate.id) - (select COUNT(evaluateThree.id) from tb_marketing_evaluate evaluateThree
INNER JOIN tb_hotel_info hotel ON hotel.id = evaluateThree.place_id and hotel.status = #{params.status}
evaluateThree.evaluate_time like concat(left((SELECT DATE_SUB(evaluate.evaluate_time,INTERVAL 1 MONTH)),7),'%') and evaluateThree.equip_status = 1 and evaluateThree.deleted = 0
))/(select COUNT(evaluateFour.id) from tb_marketing_evaluate evaluateFour
INNER JOIN tb_hotel_info hotel ON hotel.id = evaluateFour.place_id and hotel.status = #{params.status}
evaluateFour.evaluate_time like concat(left((SELECT DATE_SUB(evaluate.evaluate_time,INTERVAL 1 MONTH)),7),'%') and evaluateFour.equip_status = 1 and evaluateFour.deleted = 0
)),3)*100 as sequential from tb_marketing_evaluate evaluate
INNER JOIN tb_hotel_info hotel ON hotel.id = evaluate.place_id and hotel.status = #{params.status}
evaluate.evaluate_time >= CONCAT(YEAR(now()),'-01-01 00:00:00') and evaluate.evaluate_time <= CONCAT(YEAR(now()),'-12-31 23:59:59') and evaluate.equip_status = 1 and evaluate.deleted = 0
GROUP BY DATE_FORMAT(evaluate.evaluate_time,#{params.sqlDateFormat})

SELECT DATE_FORMAT(evaluate.evaluate_time,#{params.sqlDateFormat}) as time, ROUND(SUM(CASE WHEN evaluate.evaluate_type = 0 THEN 1 ELSE 0 END)/COUNT(evaluate.id),3)*100 as count, ROUND((((ROUND(SUM(CASE WHEN evaluate.evaluate_type = 0 THEN 1 ELSE 0 END)/COUNT(evaluate.id),3)*100) - (select ROUND(SUM(CASE WHEN evaluateOne.evaluate_type = 0 THEN 1 ELSE 0 END)/COUNT(evaluateOne.id),3)*100 from tb_marketing_evaluate evaluateOne
INNER JOIN tb_hotel_info hotel ON hotel.id = evaluateOne.place_id and hotel.status = #{params.status}
evaluateOne.evaluate_time like concat(left((SELECT DATE_SUB(evaluate.evaluate_time,INTERVAL 1 YEAR)),7),'%') and evaluateOne.equip_status = 1 and evaluateOne.deleted = 0
))/(select ROUND(SUM(CASE WHEN evaluateTwo.evaluate_type = 0 THEN 1 ELSE 0 END)/COUNT(evaluateTwo.id),3)*100 from tb_marketing_evaluate evaluateTwo
INNER JOIN tb_hotel_info hotel ON hotel.id = evaluateTwo.place_id and hotel.status = #{params.status}
evaluateTwo.evaluate_time like concat(left((SELECT DATE_SUB(evaluate.evaluate_time,INTERVAL 1 YEAR)),7),'%') and evaluateTwo.equip_status = 1 and evaluateTwo.deleted = 0
)),3)*100 as same, ROUND((((ROUND(SUM(CASE WHEN evaluate.evaluate_type = 0 THEN 1 ELSE 0 END)/COUNT(evaluate.id),3)*100) - (select ROUND(SUM(CASE WHEN evaluateThree.evaluate_type = 0 THEN 1 ELSE 0 END)/COUNT(evaluateThree.id),3)*100 from tb_marketing_evaluate evaluateThree
INNER JOIN tb_hotel_info hotel ON hotel.id = evaluateThree.place_id and hotel.status = #{params.status}
evaluateThree.evaluate_time like concat(left((SELECT DATE_SUB(evaluate.evaluate_time,INTERVAL 1 MONTH)),7),'%') and evaluateThree.equip_status = 1 and evaluateThree.deleted = 0
))/(select ROUND(SUM(CASE WHEN evaluateFour.evaluate_type = 0 THEN 1 ELSE 0 END)/COUNT(evaluateFour.id),3)*100 from tb_marketing_evaluate evaluateFour
INNER JOIN tb_hotel_info hotel ON hotel.id = evaluateFour.place_id and hotel.status = #{params.status}
evaluateFour.evaluate_time like concat(left((SELECT DATE_SUB(evaluate.evaluate_time,INTERVAL 1 MONTH)),7),'%') and evaluateFour.equip_status = 1 and evaluateFour.deleted = 0
)),3)*100 as sequential from tb_marketing_evaluate evaluate
INNER JOIN tb_hotel_info hotel ON hotel.id = evaluate.place_id and hotel.status = #{params.status}
evaluate.evaluate_time >= CONCAT(YEAR(now()),'-01-01 00:00:00') and evaluate.evaluate_time <= CONCAT(YEAR(now()),'-12-31 23:59:59') and evaluate.equip_status = 1 and evaluate.deleted = 0
GROUP BY DATE_FORMAT(evaluate.evaluate_time,#{params.sqlDateFormat})
