SELECT COUNT(id) as currentMonthNum FROM tb_project_info

    deleted = 0


            AND create_time BETWEEN #{params.beginDate} AND #{params.endDate}

select tpi.*, tami.status audit_status, tami.create_time audit_time
from tb_project_info tpi
         left join (select * from tb_audit_manage_info where deleted = 0) tami on tpi.id = tami.source_id

    tpi.deleted = 0

        and tpi.update_time >= #{query.updateBeginTime,jdbcType=TIMESTAMP}


        and tpi.update_time <= #{query.updateEndTime,jdbcType=TIMESTAMP}


        and tpi.project_name like concat('%', #{query.projectName}, '%')


        and tpi.status in

            #{item}



        and tpi.area_code in

            #{item}



        and tpi.status != 4


        and tpi.company_id = #{query.companyId,jdbcType=VARCHAR}


        and tpi.area_code like concat('%', #{query.areaCode,jdbcType=VARCHAR}, '%')


        and tami.status in

            #{item}



order by sort_num, ifnull(tpi.update_time, tpi.create_time) desc

select tpi.*, taml.status audit_status, taml.create_time audit_time from tb_project_info tpi left join (select l.* from tb_audit_manage_log l right join (select max(l.id) max_id, source_id from tb_audit_manage_log l left join (select * from tb_audit_manage_process where deleted = 0) p on l.process_id = p.id where l.deleted = 0 and ( ( p.user_ids like concat('%"', #{userId}, '"%') and audit_user is null ) or audit_user = #{userId} ) group by source_id) l1 on l.id = l1.max_id) taml on tpi.id = taml.source_id
tpi.deleted = 0 and taml.deleted = 0
and tpi.update_time >= #{query.updateBeginTime,jdbcType=TIMESTAMP}
and tpi.update_time <= #{query.updateEndTime,jdbcType=TIMESTAMP}
and tpi.project_name like concat('%', #{query.projectName}, '%')
and tpi.status in
#{item}
and tpi.area_code in
#{item}
and tpi.area_code like concat('%', #{query.areaCode,jdbcType=VARCHAR}, '%')
and taml.status in
#{item}
order by sort_num, ifnull(tpi.update_time, tpi.create_time) desc

select tpi.*, tami.status audit_status, tami.create_time audit_time
from tb_project_info tpi
left join (select * from tb_audit_manage_info where deleted = 0) tami on tpi.id = tami.source_id

    tpi.deleted = 0 and tpi.status != 4

        and tpi.update_time >= #{query.updateBeginTime,jdbcType=TIMESTAMP}


        and tpi.update_time <= #{query.updateEndTime,jdbcType=TIMESTAMP}


        and tpi.area_code in

            #{item}



        and tpi.area_code = #{query.areaCode,jdbcType=VARCHAR}


        and tami.status in

            #{item}



order by is_top desc ,sort_num ,view_num desc , ifnull(tpi.update_time, tpi.create_time) desc limit 10
